const items = $input.all();
const openAIData = $("Format Name").all();

const updatedItems = items.map((item, index) => {
  const fullName = openAIData[index]?.json?.message?.content;
  if (typeof fullName === "string" && fullName.trim() !== "") {
    // Split the full name into an array of words
    const nameParts = fullName.trim().split(" ");
    // Take the first word as firstName
    const firstName = nameParts[0];
    // Join all remaining words as lastName
    const lastName = nameParts.slice(1).join(" ");
    // Only assign if both firstName and lastName are non-empty
    if (firstName && lastName) {
      item.json.firstName = firstName;
      item.json.lastName = lastName;
    } else if (firstName) {
      // If no lastName (e.g., single-word name), assign only firstName
      item.json.firstName = firstName;
      item.json.lastName = "";
    }
  }
  return item;
});

return updatedItems;

