const items = $input.all();

const updatedItems = items.map((item) => {
  const lening_waarde_ratio = item.json.body.lening_waarde_ratio;
  const energie_label = item.json.body.energie_label;

  if (lening_waarde_ratio !== "NHG") {
    item.json.body.lening_waarde_ratio = lening_waarde_ratio.replace("%", "");
  }

  switch (energie_label) {
    case "A":
      item.json.body.energie_label = "a";
      break;
    case "B":
      item.json.body.energie_label = "b";
      break;
    case "C-G":
      item.json.body.energie_label = "c";
      break;
  }

  return item;
});

return updatedItems;

const items = $input.all();
const infoData = $("Info Ontvangen").all();
const updatedItems = items.map((item, index) => {
  const fullName = infoData[index]?.json?.body?.full_name;
  if (typeof fullName === "string" && fullName.trim() !== "") {
    // Capitalize each word in the full name
    const capitalizedName = fullName
      .trim()
      .split(" ")
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(" ");
    const nameParts = capitalizedName.split(" ");
    const firstName = nameParts[0];
    const lastName = nameParts.slice(1).join(" ");
    if (firstName && lastName) {
      item.json.firstName = firstName;
      item.json.lastName = lastName;
    } else if (firstName) {
      item.json.firstName = firstName;
      item.json.lastName = "";
    }
  }
  return item;
});
return updatedItems;

const items = $input.all();
const updatedItems = items.map((item) => {
  const body = item.json.body;
  if (body && body.lening_waarde_ratio !== undefined) {
    const lening_waarde_ratio = body.lening_waarde_ratio;
    if (lening_waarde_ratio !== "NHG") {
      item.json.body.lening_waarde_ratio = lening_waarde_ratio.replace("%", "");
    }
  }
  const momentele_rente = $input.first().json.output?.savingOpportunity1?.momenteleRente ?? "";
  const besparing1 = $input.first().json.output?.savingOpportunity1?.rate ?? "";
  item.json.momentele_renteint = momentele_rente.replace("%", "");
  item.json.besparing1 = besparing1.replace("%", "");
  return item;
});
return updatedItems;

